00734 Instance* AllEmulators::getInstance(std::string globalId)
00735 {
00736     for (int i = 0;i<allInstances.size();++i) {
00737         Instance*temp= getInstance(i);
00738         if (temp->getGlobalId() == globalId) {
00739             return temp;
00740         }
00741     }
00742     return nullptr;
00743 }
