00745 int AllEmulators::getFavoriteInstanceIndex(std::string globalId)
00746 {
00747     for (int i = 0; i < allInstances.size(); ++i) {
00748         Instance*temp = getInstance(i);
00749         if (temp->getGlobalId() == globalId) {
00750             return i;
00751         }
00752     }
00753     return -1;
00754 }
