00500 void MyFrame::CalcCreateInsertInFlowPanel(wxPoint mouseClick,FlowTagPanel* flowPanel) {
00501     wxSize tagLineSize = flowPanel->getLineSizer()->GetSize();
00502     wxPoint tagPanelPos = flowPanel->GetPosition();
00503     wxSize panelSize = flowPanel->GetSize();
00504     int lineHeight = tagLineSize.y;
00505     int panelHeight = panelSize.y;
00506     int mouseHeight = mouseClick.y;
00507     int half = lineHeight / 2;
00508 
00509     if (mouseHeight < half) {
00510         CreateInsertPanel(tagToAdd, flowPanel, ABOVE);
00511     }
00512     else if (mouseHeight > half && mouseHeight < lineHeight) {
00513         std::vector<TagPanel*> listChildren = flowPanel->getChildrenTags();
00514         if (!listChildren.empty()) {
00515             CreateInsertPanel(tagToAdd, listChildren.front(), ABOVE);
00516         }
00517     }
00518     else if (mouseHeight > panelHeight - lineHeight && mouseHeight < panelHeight - half) {
00519         std::vector<TagPanel*> listChildren = flowPanel->getChildrenTags();
00520         if (!listChildren.empty()) {
00521             CreateInsertPanel(tagToAdd, listChildren.back(), UNDER);
00522         }
00523     }
00524     else if (mouseHeight > panelHeight - half && mouseHeight < panelHeight) {
00525         CreateInsertPanel(tagToAdd, flowPanel, UNDER);
00526     }
00527     resetInsertHover();
00528 }
00529 void MyFrame::CalcCreateInsertInPanel(wxPoint mouseClick,TagPanel* panel) {
00530     int mouseHeight = mouseClick.y;
00531     wxPoint tagPanelPos = panel->GetPosition();
00532     wxSize panelSize = panel->GetSize();
00533     int panelHeight = panelSize.y;
00534     if (mouseHeight < panelHeight / 2) {
00535         CreateInsertPanel(tagToAdd, panel, ABOVE);
00536     }
00537     else if (mouseHeight > panelHeight / 2 && mouseHeight < panelHeight) {
00538         CreateInsertPanel(tagToAdd, panel, UNDER);
00539     }
00540     resetInsertHover();
00541 }
