00594         FlowTagPanel* newFlowPanel = nullptr;
00595         switch (tag->myType) {
00596         case LOOPTAG:
00597             newFlowPanel = new LoopTagPanel(parentTag);
00598             break;
00599         case IFTAG:
00600             newFlowPanel = new IfTagPanel(parentTag);
00601             break;
00602         case MAINTAG:
00603             newFlowPanel = new MainTagPanel(parentTag);
00604             break;
00605         case LOADTAG:
00606             newPanel = new TagPanel(parentTag);
00607             break;
00608         case STORETAG:
00609             newPanel = new TagPanel(parentTag);
00610             break;
00611         case BOOLTAG:
00612             newPanel = new BoolTagPanel(parentTag);
00613             break;
00614         default:
00615             newPanel = new TagPanel(parentTag);
00616             break;
00617         }
00618         if (newFlowPanel) {
00619             BindFlowTagPanelEvents(newFlowPanel);
00620             newPanel = newFlowPanel;
00621         }
00622         else if (newPanel) {
00623             BindTagPanelEvents(newPanel);
00624         }
00625         newPanel->setTag(tag);
00626         return newPanel;
00627     }
00628     return nullptr;
00629 }
