00631 TagPanel* MyFrame::CreatePanelFromTagRec(wxWindow* parentTag, const std::shared_ptr<Tag>& tag) {
00632     
00633     auto flowParent = dynamic_cast<FlowTagPanel*>(parentTag);
00634     auto newPanel = CreatePanelFromTag(parentTag, tag);
00635     if (newPanel) {
00636         auto newFlowPanel = getFlowTagPanel(newPanel);
00637         if (newFlowPanel)CreatePanelFromTagRec(newFlowPanel, getFlowTag(newFlowPanel->getTag())->nestedTags);
00638         if (flowParent && newPanel)flowParent->addChild(newPanel);
00639     }
00640     return newPanel;
00641 }
