00643 void MyFrame::CreatePanelFromTagRec(wxWindow* parentTag, const std::vector<std::shared_ptr<Tag>>& listTag)
00644 {
00645     for (auto& tag : listTag) {
00646         CreatePanelFromTagRec(parentTag, tag);
00647     }
00648 }
