00689 void MyFrame::OnMouseMoved(wxMouseEvent& evt){
00690     if (placingTag) {
00691         wxObject* obj = evt.GetEventObject();
00692         TagPanel* panel = dynamic_cast<TagPanel*>(obj);
00693         FlowTagPanel* flowPanel = dynamic_cast<FlowTagPanel*>(obj);
00694         wxPoint mousePos = evt.GetPosition();
00695         int mouseHeight = mousePos.y;
00696         
00697         if (panel) {
00698             wxPoint previousPoint = drawPoint;
00699             HoverPosition h=NOHOVER;
00700             HoverPosition previousHover= panel->previousHover;
00701             TagPanel* parent = panel->getParentTag();
00702             std::vector<TagPanel*> tempHover;
00703             if (flowPanel) {
00704                 wxSize panelSize = flowPanel->GetSize();
00705                 int panelHeight = panelSize.GetHeight();
00706                 wxSize tagLineSize = flowPanel->getLineSizer()->GetSize();
00707                 wxSize bottomSize;
00708                 if (!flowPanel->isMinimized()) {
00709                     bottomSize = flowPanel->getBottomSpacer()->GetSize();
00710                 }
00711                 int bottomHeight = bottomSize.GetHeight();
00712                 int lineHeight = tagLineSize.y;
00713                 int half = lineHeight / 2;
00714                 if (mouseHeight < half) {
00715                     h = TOPHOVER;
00716                 }
00717                 else if (mouseHeight > half && mouseHeight < lineHeight) {
00718                     h = MIDTOPHOVER;
00719                 }
00720                 else if (mouseHeight > panelHeight - bottomHeight-1 && mouseHeight < panelHeight - (bottomHeight/2)) {
00721                     h = MIDBOTHOVER;
00722                 }
00723                 else if (mouseHeight > panelHeight - (bottomHeight/2) && mouseHeight < panelHeight) {
00724                     h = BOTTOMHOVER;
00725                 }
00726                 panel = flowPanel;
00727             }
00728             else if (panel) {
00729                 wxSize panelSize = panel->GetSize();
00730                 int panelHeight = panelSize.GetHeight();
00731                 if (mouseHeight < (panelHeight / 2)+1) {
00732                     h = TOPHOVER;
00733                 }
00734                 else if (mouseHeight > panelHeight / 2 && mouseHeight < panelHeight) {
00735                     h = BOTTOMHOVER;
00736                 }
00737             }
00738             if (previousHover != h)HoverSwitch = true;
00739             if (HoverSwitch) {
00740                 if (hoverNeighbour) {
00741                     hoverNeighbour->isInserting = false;
00742                     hoverNeighbour->Refresh();
00743                 }
00744                 panel->isInserting = true;
00745                 if(hoverNeighbour!=panel)panel->Refresh();
00746 
00747                 setNeighboursPanel(panel, h);
00748                 if(hoverNeighbour)hoverNeighbour->Refresh();
00749                 HoverSwitch = false;
00750             }
00751         }
00752     }
00753 }
00754 
00755 void MyFrame::setNeighboursPanel(TagPanel*panel,HoverPosition currentHover) {
00756     panel->setInsertPoint(currentHover);
00757     auto parent = panel->getParentTag();
00758     FlowTagPanel* flowTag = dynamic_cast<FlowTagPanel*>(panel);
00759     switch (currentHover) {
00760     case TOPHOVER:
00761         hoverNeighbour = panel->getPreviousTag();
00762         if (hoverNeighbour) {
00763             hoverNeighbour->isInserting = true;
00764             hoverNeighbour->setInsertPoint(BOTTOMHOVER);
00765         }
00766         else if (parent) {
00767             parent->isInserting = true;
00768             parent->setInsertPoint(MIDTOPHOVER);
00769             hoverNeighbour = parent;
00770         }
00771         break;
00772     case MIDTOPHOVER:
00773         if (flowTag) {
00774             hoverNeighbour = flowTag->getFirstTag();
00775             if (hoverNeighbour) {
00776                 hoverNeighbour->isInserting = true;
00777                 hoverNeighbour->setInsertPoint(TOPHOVER);
00778             }
00779         }
00780         break;
00781     case MIDBOTHOVER:
00782         if (flowTag) {
00783             hoverNeighbour = flowTag->getLastTag();
00784             if (hoverNeighbour) {
00785                 hoverNeighbour->isInserting = true;
00786                 hoverNeighbour->setInsertPoint(BOTTOMHOVER);
00787             }
00788         }
00789         break;
00790     case BOTTOMHOVER:
00791         hoverNeighbour = panel->getNextTag();
00792         if (hoverNeighbour) {
00793             hoverNeighbour->isInserting = true;
00794             hoverNeighbour->setInsertPoint(TOPHOVER);
00795         }
00796         else if (parent) {
00797             parent->isInserting = true;
00798             parent->setInsertPoint(MIDBOTHOVER);
00799             hoverNeighbour = parent;
00800         }
00801         break;
00802     }
00803 }
