00825         newPanel->Bind(wxEVT_ENTER_WINDOW, &MyFrame::OnTagPanelEnter, this);
00826         newPanel->Bind(wxEVT_LEAVE_WINDOW, &MyFrame::OnTagPanelLeave, this);
00827         newPanel->Bind(wxEVT_LEAVE_WINDOW, &MyFrame::OnTagPanelLeave, this);
00828         auto cloneButton = newPanel->getCloneButton();
00829         if (cloneButton) {
00830             cloneButton->Bind(wxEVT_LEFT_DOWN, &MyFrame::OnDuplicateClick, this);
00831         }  
00832         auto cutButton = newPanel->getCutButton();
00833         if (cutButton) {
00834             cutButton->Bind(wxEVT_LEFT_DOWN, &MyFrame::OnCutClick, this);
00835         }
00836     }
00837 }
