00921 void MyFrame::ResizeImage(wxImage& image,const wxSize& size) {
00922     wxSize imageSize = image.GetSize();
00923     int boxWidth = size.GetWidth();
00924     int boxHeight = size.GetHeight();
00925     int imageWidth = imageSize.GetWidth();
00926     int imageHeight = imageSize.GetHeight();
00927     float widthRatio = float(boxWidth) / float(imageWidth);
00928     float heightRatio = float(boxHeight) / float(imageHeight);
00929     float ratio = std::min(widthRatio,heightRatio);
00930     int newWidth =imageWidth*ratio;
00931     int newHeight = imageHeight*ratio;
00932     image.Rescale(newWidth, newHeight);
00933 }
