00412 void FlowTagPanel::setInsertPoint(HoverPosition hovPos)
00413 {
00414     wxSize tagLineSize = getLineSizer()->GetSize();
00415     auto bottomSpacer = getBottomSpacer();
00416     wxSize bottomSize;
00417     if (!isMinimized()) {
00418         bottomSize = getBottomSpacer()->GetSize();
00419     }
00420     int bottomHeight = bottomSize.y;
00421     int lineHeight = tagLineSize.y;
00422     switch (hovPos) {
00423     case TOPHOVER:
00424         insertPoint = wxPoint(0, 0);
00425         break;
00426     case MIDTOPHOVER:
00427         insertPoint = wxPoint(0, 2*lineHeight/3);
00428         break;
00429     case MIDBOTHOVER:
00430         insertPoint= wxPoint(0, GetSize().GetHeight() - bottomHeight);
00431         break;
00432     case BOTTOMHOVER:
00433         insertPoint = wxPoint(0, GetSize().GetHeight() - bottomHeight/3);
00434         break;
00435     }
00436     previousHover = hovPos;
00437 }
