00925 bool FlowTagPanel::isEmpty()
00926 {
00927     auto listChildren = getChildrenTags();
00928     int size = listChildren.size();
00929     if (size == 0)return true;
00930     else if (size == 1) {
00931         if (listChildren.front() == emptyTagPanel)return true;
00932         else return false;
00933     }
00934     return false;
00935 }
