01041 void FlowTagPanel::addChild(TagPanel* childrenTag)
01042 {
01043     if (childrenTag){
01044         if (nbFlowParents < MAXFLOW) {
01045             insertChild(childrenTag, childrenSizer->GetItemCount());
01046         }
01047     }
01048 }
01049 
01050 void FlowTagPanel::switchFlowTagVisibility(wxMouseEvent& evt) {
01051     wxButton* but = dynamic_cast<wxButton*>(evt.GetEventObject());
01052     if (but) {
01053         FlowTagPanel* parentPanel = dynamic_cast<FlowTagPanel*>(but->GetParent());
01054         if (parentPanel) {
01055             switch (parentPanel->childrenVisible()) {
01056             case false:
01057                 parentPanel->setChildrenVisibility(true);
01058                 break;
01059             case true:
01060                 parentPanel->setChildrenVisibility(false);
01061                 break;
01062             }
01063             parentPanel->updateLayout();
01064         }
01065     }
01066 
01067 }
01068 
01069 void FlowTagPanel::insertChild(TagPanel* target,TagPanel* newPanel,AddPosition pos)
01070 {
01071     int offset=0;
01072     switch (pos) {
01073     case ABOVE:
01074         offset = 0;
01075         break;
01076     case UNDER:
01077         offset = 1;
01078         break;
01079     }
01080     wxSizerItem* item=getSizerItem(target);
01081     if (item) {
01082         int targetIndex = getIndexOf(childrenSizer, item);
01083         insertChild(newPanel, targetIndex + offset);
01084     }
01085 }
01086 
01087 void FlowTagPanel::insertChild(TagPanel* newPanel, int index)
01088 {
01089     if (containsChild(newPanel))return;
01090     wxSizerFlags insertFlags = wxSizerFlags().Expand().Border(wxLEFT, leftBorder);
01091     childrenSizer->Insert(index, newPanel, insertFlags);
01092     if (isEmpty())showEmptyPanel();
01093     else hideEmptyPanel();
01094 }
01095 
01096 void FlowTagPanel::swapChildren(TagPanel* panel1, TagPanel* panel2) {
01097     if (containsChild(panel1) && containsChild(panel2)) {
01098         int index2=getIndex(panel2);
01099         detachPanel(panel2);
01100         childrenSizer->Replace(panel1, panel2);
01101         insertChild(panel1, index2);
01102         childrenSizer->Layout();
01103     }
01104 }
01105 
01106 bool FlowTagPanel::containsChild(TagPanel* panel)
01107 {
01108     return getIndex(panel)!=-1;
01109 }
