01124 
01125 std::vector<TagPanel*> FlowTagPanel::getChildrenTags()const
01126 {
01127     std::vector<TagPanel*> windowList;
01128 
01129     auto& sizerChildren = this->childrenSizer->GetChildren();
01130     for (auto& item : sizerChildren) {
01131         auto child = dynamic_cast<TagPanel*>(item->GetWindow());
01132         if(child)windowList.push_back(child);
01133     }
01134     return windowList;
01135 }
