00083 
00084     template<typename T>
00085     void addVariable(std::string name, T value);
00086 
00087     template<typename T>
00088     T getValue(std::string name);
00089 };
00090 
00091 template<typename T>
00092 void UserVariables::addVariable(std::string name, T value) {
00093     variables[name] = new T(value);
00094 }
00095 
00096 template<typename T>
00097 T UserVariables::getValue(std::string name) {
00098     auto it = variables.find(name);
00099     if (it != variables.end()) {
00100         return *static_cast<T*>(it->second);
00101     }
00102     return T();
00103 }
