00763 template<typename T>
00764 inline std::vector<T> StoreToken::test(int depth)
00765 {
00766     if (depth > 0) {
00767         // Create a vector containing the result of a recursive call for the next layer
00768         return test<std::vector<T>>(depth - 1);
00769     }
